740: static grpc_error* kick_one_worker(grpc_pollset_worker* specific_worker) {
741:   GPR_TIMER_SCOPE("kick_one_worker", 0);
742:   pollable* p = specific_worker->pollable_obj;
743:   grpc_core::MutexLock lock(&p->mu);
744:   GPR_ASSERT(specific_worker != nullptr);
745:   if (specific_worker->kicked) {
746:     if (grpc_polling_trace.enabled()) {
747:       gpr_log(GPR_INFO, "PS:%p kicked_specific_but_already_kicked", p);
748:     }
749:     GRPC_STATS_INC_POLLSET_KICKED_AGAIN();
750:     return GRPC_ERROR_NONE;
751:   }
752:   if (gpr_tls_get(&g_current_thread_worker) == (intptr_t)specific_worker) {
753:     if (grpc_polling_trace.enabled()) {
754:       gpr_log(GPR_INFO, "PS:%p kicked_specific_but_awake", p);

1707:         if (res >= 0) res++;
1708:       }
1709:     } else if (!skip_poll && fds[i].fd >= 0 && result->completed) {
1710:       fds[i].revents = result->fds[idx].revents;
1711:       idx++;
1712:     }
1713:   }
1714: 
1715:   gpr_free(fd_cvs);
1716:   gpr_free(pollcv);
1717:   if (result) {
1718:     decref_poll_result(result);
1719:   }
1720: 
1721:   gpr_mu_unlock(&g_cvfds.mu);
1722: 
1723:   return res;
1724: }
