77:   int after_pos = s.find(after);
78:   ASSERT_NE(std::string::npos, before_pos);
79:   ASSERT_NE(std::string::npos, after_pos);
80:   EXPECT_LT(before_pos, after_pos)
81:       << before << " is not before " << after << " in " << s;
82: }
