54:         FloatT index_bias =
55:             static_cast<FloatT>(index_product % 113 - negative_bias) /
56:             static_cast<FloatT>(256.0f);
57:         return static_cast<FloatT>(generator(*engine) - 1.0625f) + index_bias;
58:       }));
59: }
