130:   void Deallocate(const Alloc& a) {
131:     auto iter = std::lower_bound(live_allocs_.begin(), live_allocs_.end(), a);
132:     CHECK(iter != live_allocs_.end());
133:     CHECK(*iter == a);
134:     live_allocs_.erase(iter);
135:   }
