96: void DeviceResolverDistributed::RefreshRemoteAttributes(
97:     const string& device, const string& task, const StatusCallback& done) {
98:   GetStatusRequest* req = new GetStatusRequest;
99:   GetStatusResponse* resp = new GetStatusResponse;
100:   WorkerInterface* worker = worker_cache_->CreateWorker(task);
101:   CHECK(worker) << "Failed to get worker for " << task;
102:   worker->GetStatusAsync(
103:       req, resp, [this, device, task, req, resp, worker, done](Status s) {
104:         if (s.ok()) {
105:           mutex_lock l(mu_);
106:           for (const DeviceAttributes& da : resp->device_attributes()) {
107:             attr_table_[da.name()] = da;
108:           }
109:         }
110:         done(s);
111:         delete req;
112:         delete resp;

163:       }
164:     };
165:     for (const string& filter : device_filters) {
166:       process_filter(filter);
167:     }
168:     // Enumerates all known workers' target. A target name is a
169:     // prefix of a device name. E.g., /job:mnist/replica:0/task:10.
170:     CHECK_GT(env_->local_devices.size(), 0) << "No local devices provided.";
171:     const string& local_device_name = env_->local_devices[0]->name();
172:     std::vector<string> workers;
173:     worker_cache->ListWorkers(&workers);
174:     if (filters_.empty()) {
175:       std::swap(workers, targets_);
176:     } else {
177:       for (const string& name : workers) {
178:         if (MatchFilters(name) ||
179:             DeviceNameUtils::IsSameAddressSpace(name, local_device_name)) {
180:           targets_.push_back(name);
181:         }
182:       }
183:     }

1374:     }
1375: 
1376:     if (graph_version_ != req->current_graph_version()) {
1377:       return errors::Aborted("Current version is ", graph_version_,
1378:                              " but caller expected ",
1379:                              req->current_graph_version(), ".");
1380:     }
1381: 
1382:     CHECK(execution_state_);
1383:     TF_RETURN_IF_ERROR(
1384:         execution_state_->Extend(req->graph_def(), &extended_execution_state));
1385: 
1386:     CHECK(extended_execution_state);
1387:     // The old execution state will be released outside the lock.
1388:     execution_state_.swap(extended_execution_state);
1389:     ++graph_version_;
1390:     resp->set_new_graph_version(graph_version_);
1391:   }
1392:   return Status::OK();
1393: }
