2007: void MasterSession::GarbageCollect() {
2008:   {
2009:     mutex_lock l(mu_);
2010:     closed_ = true;
2011:     garbage_collected_ = true;
2012:   }
2013:   cancellation_manager_.StartCancel();
2014:   Unref();
