2486: bool ConstantFolding::ReduceDivToReciprocalMul(GraphDef* optimized_graph,
2487:                                                NodeDef* node) {
2488:   // Strength reduce floating point division by a constant Div(x, const) to
2489:   // multiplication by the reciprocal Mul(x, Reciprocal(const)). This in turn
2490:   // will be constant folded to Mul(x, 1.0/const).
2491:   if (node->input_size() >= 2 && (IsRealDiv(*node) || IsDiv(*node))) {
2492:     const string& const_input = node->input(1);
2493:     const NodeDef* denom = node_map_->GetNode(const_input);
2494:     CHECK(denom != nullptr);
2495:     if (!IsReallyConstant(*denom)) {
2496:       return false;
2497:     }
2498:     if (node->attr().count("T") == 0) {
2499:       return false;
2500:     }
2501:     DataType type = node->attr().at("T").type();
2502:     if (IsDiv(*node) &&
2503:         !(DataTypeIsFloating(type) || DataTypeIsComplex(type))) {
2504:       return false;
2505:     }

2799:   const int num_non_control_inputs = NumNonControlInputs(*node);
2800:   if (IsAggregate(*node) && IsCommutative(*node) &&
2801:       num_non_control_inputs > 2) {
2802:     const int num_control_inputs = node->input_size() - num_non_control_inputs;
2803:     std::vector<int> const_inputs;
2804:     std::vector<int> nonconst_inputs;
2805:     for (int i = 0; i < node->input_size(); ++i) {
2806:       const string& input = node->input(i);
2807:       const NodeDef* input_node = node_map_->GetNode(NodeName(input));
2808:       CHECK(input_node != nullptr) << input;
2809:       if (!IsControlInput(input) && IsReallyConstant(*input_node)) {
2810:         const_inputs.push_back(i);
2811:       } else {
2812:         // Non-const and control inputs.
2813:         nonconst_inputs.push_back(i);
2814:       }
2815:     }
2816:     // Promote AccumulateNV2 with all constant inputs to AddN, since it is
2817:     // a fake node that cannot be constant folded by itself.
2818:     if (const_inputs.size() == num_non_control_inputs &&
2819:         node->op() == "AccumulateNV2") {

75:   }
76:   if (!fetch_nodes_known_) {
77:     // The output values of this node may be needed.
78:     return false;
79:   }
80:   const NodeDef* input = node_map_->GetNode(NodeName(node.input(0)));
81:   CHECK(input != nullptr) << "node = " << node.name()
82:                           << " input = " << node.input(0);
83:   // Don't remove Identity nodes corresponding to Variable reads or following
84:   // Recv.
85:   if (IsVariable(*input) || IsRecv(*input)) {
86:     return false;
87:   } else if (IsSwitch(*input)) {
88:     // Don't turn Identity nodes following Switch into NoOp or remove them
89:     // if it requires anchoring a control dependencies the Switch node, which
90:     // is not valid.
91:     if (str_util::StartsWith(node.name(), kConstantFoldingCtrl)) {
92:       // TODO(rmlarsen): Try to remove this artificial contraint.
93:       return false;
94:     }
