250:       default:
251:         // Do not close stdin/out/err, instead redirect them to /dev/null so
252:         // their file descriptors remain unavailable for reuse by open(), etc.
253:         if (i <= CHAN_STDERR) {
254:           if (devnull_fd < 0) {
