77:   if (result == 0) {
78:     fprintf(stderr, "Failed to read from child stdout: %zu %s\n", result,
79:             strerror(errno));
80:     return EXIT_FAILURE;
81:   }
82:   buffer[result] = '\0';
83:   int status = pclose(f);
84:   if (status == -1) {
85:     fprintf(stderr, "Failed to close popen child: %s\n", strerror(errno));
