152: double NominalCPUFrequency() {
153:   DWORD data;
154:   DWORD data_size = sizeof(data);
155:   #pragma comment(lib, "shlwapi.lib")  // For SHGetValue().
156:   if (SUCCEEDED(
157:           SHGetValueA(HKEY_LOCAL_MACHINE,
158:                       "HARDWARE\\DESCRIPTION\\System\\CentralProcessor\\0",
159:                       "~MHz", nullptr, &data, &data_size))) {
160:     return data * 1e6;  // Value is MHz.
161:   }
162:   return 1.0;
163: }
