144:     std::unique_ptr<OpLogProto> op_log_ptr;
145:     op_log_ptr.reset(new OpLogProto());
146:     op_log_ptr->ParseFromString(*op_log);
147:     tf_stat->AddOpLogProto(std::move(op_log_ptr));
148:   }
149:   return tf_stat->run_coverage();
150: }

