97:   for (int i = 0; i < input_length_; ++i) {
98:     double melf = FreqToMel(i * hz_per_sbin);
99:     if ((i < start_index_) || (i > end_index_)) {
100:       band_mapper_[i] = -2;  // Indicate an unused Fourier coefficient.
101:     } else {
102:       while ((center_frequencies_[channel] < melf) &&
103:              (channel < num_channels_)) {
104:         ++channel;
105:       }
106:       band_mapper_[i] = channel - 1;  // Can be == -1
107:     }
