203:   const_op->set_op("Const");
204:   const_op->set_name(name);
205:   (*const_op->mutable_attr())["dtype"].set_type(DT_FLOAT);
206:   auto* tensor = (*const_op->mutable_attr())["value"].mutable_tensor();
207:   CHECK(model.HasArray(name));
208:   const auto& input_array = model.GetArray(name);
209:   const auto& input_shape = input_array.shape();
210:   CHECK(input_array.buffer);
211:   CHECK(input_array.buffer->type == ArrayDataType::kFloat);
212:   const float* input_data =
213:       input_array.GetBuffer<ArrayDataType::kFloat>().data.data();
214:   ExportFloatArray(input_shape, input_data, tensor,
215:                    LegacyScalarPolicy::kAvoidLegacyScalars);
216: }
