322: string WalkUpToConstantArray(const Model& model, const string& name) {
323:   const Array& original_array = model.GetArray(name);
324:   if (original_array.buffer) {
325:     return name;
326:   }
327:   const auto* op = GetOpWithOutput(model, name);
328:   CHECK(op);
329:   CHECK(op->type == OperatorType::kFakeQuant);
330:   const string& input_of_fakequant_name = op->inputs[0];
331:   const Array& input_of_fakequant = model.GetArray(input_of_fakequant_name);
332:   CHECK(input_of_fakequant.buffer);
333:   return input_of_fakequant_name;
334: }
