2118:         if (input_iter == input_frame->iteration_count) {
2119:           input_frame->IncrementIteration(&impl_->gview_, ready);
2120:         }
2121:         output_iter = input_iter + 1;
2122:       }
2123:     }
2124:     if (output_frame != nullptr) {
2125:       // This is the case when node is not Enter, Exit, or NextIteration.
2126:       DCHECK(input_frame == output_frame);
2127:       output_frame->ActivateNodes(item, is_dead, output_iter, outputs, ready);
2128:     }
2129:     is_frame_done = input_frame->DecrementOutstandingOpsLocked(
2130:         &impl_->gview_, input_iter, ready);
2131:   }
